.board-history-wrapper {
  display: flex;
  align-items: center;
}

.board-history {
  background-color: var(--background-default);
  overflow: scroll;
  height: 25rem;
  width: calc(var(--spawn-size) *1.7);
  border: 2px solid var(--border-color);
  z-index: 9999;
}

.board-history-table thead {
  font-weight: bold;
  line-height: 1.5rem;
}

