  border-bottom: 3px solid var(--border-color);
}

.board-history-table tbody>tr {
  border-bottom: 1px solid var(--border-color);
}

.wide-history {
  grid-column: span 2;
  order: 4;
}

table {
  border-collapse: collapse;
  width: 100%;
}

table>tbody>tr>td:nth-child(2) {
  color: var(--player-color)
}

